server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { setupAuth } from "./auth";
import { storage } from "./storage";
import { insertTransactionSchema, insertUserSettingsSchema, insertSupportTicketSchema, insertChatMessageSchema } from "@shared/schema";
import { z } from "zod";
import crypto from "crypto";

export function registerRoutes(app: Express): Server {
  setupAuth(app);

  // Credit packages configuration
  const creditPackages = {
    starter: { credits: 1000, priceUSD: 10, priceINR: 800, name: "Starter Pack" },
    professional: { credits: 5000, priceUSD: 45, priceINR: 3600, name: "Professional Pack" },
    enterprise: { credits: 10000, priceUSD: 80, priceINR: 6400, name: "Enterprise Pack" }
  };

  // Get user dashboard stats
  app.get("/api/dashboard/stats", async (req, res) => {
    if (!req.isAuthenticated()) return res.sendStatus(401);

    const user = req.user!;
    const transactions = await storage.getTransactions(user.id);
    const apiUsage = await storage.getApiUsage(user.id);

    const totalSpent = transactions
      .filter(t => t.status === "completed")
      .reduce((sum, t) => sum + parseFloat(t.amount), 0);

    const creditsUsed = apiUsage.reduce((sum, u) => sum + u.creditsUsed, 0);
    const apiCalls = apiUsage.length;
    const successRate = apiUsage.length > 0 
      ? (apiUsage.filter(u => u.success).length / apiUsage.length) * 100 
      : 100;

    res.json({
      totalCredits: user.credits,
      creditsUsed,
      apiCalls,
      successRate: successRate.toFixed(1),
      totalSpent: totalSpent.toFixed(2),
    });
  });

  // Get recent activity
  app.get("/api/dashboard/activity", async (req, res) => {
    if (!req.isAuthenticated()) return res.sendStatus(401);

    const user = req.user!;
    const transactions = await storage.getTransactions(user.id);
    const apiUsage = await storage.getApiUsage(user.id);

    const activity = [
      ...transactions.slice(0, 3).map(t => ({
        type: "transaction",
        title: "Credits Purchased",
        description: `${t.credits} credits added to account`,
        time: t.createdAt,
        icon: "account_balance_wallet",
      })),
      ...apiUsage.slice(0, 2).map(u => ({
        type: "api_usage",
        title: "AI Analysis Completed",
        description: `${u.endpoint} processed successfully`,
        time: u.createdAt,
        icon: "psychology",
      }))
    ].sort((a, b) => b.time.getTime() - a.time.getTime()).slice(0, 5);

    res.json(activity);
  });

  // Get credit packages
  app.get("/api/credits/packages", (req, res) => {
    res.json(creditPackages);
  });

  // Create Razorpay order
  app.post("/api/credits/create-order", async (req, res) => {
    if (!req.isAuthenticated()) return res.sendStatus(401);

    const { packageType, currency } = req.body;

    if (!creditPackages[packageType as keyof typeof creditPackages]) {
      return res.status(400).json({ message: "Invalid package type" });
    }

    const pkg = creditPackages[packageType as keyof typeof creditPackages];
    const amount = currency === "INR" ? pkg.priceINR : pkg.priceUSD;

    try {
      // In a real implementation, you would use the Razorpay SDK here
      const orderId = `order_${crypto.randomBytes(12).toString('hex')}`;

      // Create pending transaction
      await storage.createTransaction({
        userId: req.user!.id,
        transactionId: `txn_${crypto.randomBytes(12).toString('hex')}`,
        packageName: pkg.name,
        credits: pkg.credits,
        amount: amount.toString(),
        currency,
        status: "pending",
        paymentMethod: "razorpay",
        razorpayOrderId: orderId,
        razorpayPaymentId: null,
      });

      res.json({
        orderId,
        amount: amount * 100, // Razorpay expects amount in paise/cents
        currency,
        key: process.env.RAZORPAY_KEY_ID || "rzp_test_key",
      });
    } catch (error) {
      console.error("Error creating order:", error);
      res.status(500).json({ message: "Failed to create order" });
    }
  });

  // Verify payment and update credits
  app.post("/api/credits/verify-payment", async (req, res) => {
    if (!req.isAuthenticated()) return res.sendStatus(401);

    const { razorpayOrderId, razorpayPaymentId, razorpaySignature } = req.body;

    try {
      // In a real implementation, you would verify the signature with Razorpay
      // For now, we'll simulate successful verification

      const transaction = await storage.getTransactions(req.user!.id);
      const pendingTransaction = transaction.find(t => 
        t.razorpayOrderId === razorpayOrderId && t.status === "pending"
      );

      if (!pendingTransaction) {
        return res.status(400).json({ message: "Transaction not found" });
      }

      // Update transaction status
      await storage.updateTransaction(pendingTransaction.id, {
        status: "completed",
        razorpayPaymentId,
      });

      // Add credits to user
      const user = req.user!;
      await storage.updateUser(user.id, {
        credits: user.credits + pendingTransaction.credits,
      });

      res.json({ message: "Payment verified and credits added successfully" });
    } catch (error) {
      console.error("Error verifying payment:", error);
      res.status(500).json({ message: "Failed to verify payment" });
    }
  });

  // Get user profile
  app.get("/api/profile", async (req, res) => {
    if (!req.isAuthenticated()) return res.sendStatus(401);

    const user = req.user!;
    const { password, ...userWithoutPassword } = user;
    res.json(userWithoutPassword);
  });

  // Update user profile
  app.patch("/api/profile", async (req, res) => {
    if (!req.isAuthenticated()) return res.sendStatus(401);

    try {
      const { firstName, lastName, email, phone, company } = req.body;
      const updatedUser = await storage.updateUser(req.user!.id, {
        firstName,
        lastName,
        email,
        phone,
        company,
      });

      if (!updatedUser) {
        return res.status(404).json({ message: "User not found" });
      }

      const { password, ...userWithoutPassword } = updatedUser;
      res.json(userWithoutPassword);
    } catch (error) {
      console.error("Error updating profile:", error);
      res.status(500).json({ message: "Failed to update profile" });
    }
  });

  // Get payment history
  app.get("/api/payments", async (req, res) => {
    if (!req.isAuthenticated()) return res.sendStatus(401);

    const transactions = await storage.getTransactions(req.user!.id);
    res.json(transactions);
  });

  // Get payment summary
  app.get("/api/payments/summary", async (req, res) => {
    if (!req.isAuthenticated()) return res.sendStatus(401);

    const transactions = await storage.getTransactions(req.user!.id);
    const completedTransactions = transactions.filter(t => t.status === "completed");

    const totalSpent = completedTransactions.reduce((sum, t) => sum + parseFloat(t.amount), 0);
    const totalTransactions = completedTransactions.length;
    const totalCredits = completedTransactions.reduce((sum, t) => sum + t.credits, 0);

    res.json({
      totalSpent: totalSpent.toFixed(2),
      totalTransactions,
      totalCredits,
    });
  });

  // Get user settings
  app.get("/api/settings", async (req, res) => {
    if (!req.isAuthenticated()) return res.sendStatus(401);

    const settings = await storage.getUserSettings(req.user!.id);
    if (!settings) {
      return res.status(404).json({ message: "Settings not found" });
    }

    res.json(settings);
  });

  // Update user settings
  app.patch("/api/settings", async (req, res) => {
    if (!req.isAuthenticated()) return res.sendStatus(401);

    try {
      const updateData = insertUserSettingsSchema.partial().parse(req.body);
      const updatedSettings = await storage.updateUserSettings(req.user!.id, updateData);

      if (!updatedSettings) {
        return res.status(404).json({ message: "Settings not found" });
      }

      res.json(updatedSettings);
    } catch (error) {
      console.error("Error updating settings:", error);
      res.status(500).json({ message: "Failed to update settings" });
    }
  });

  // Create support ticket
  app.post("/api/support/tickets", async (req, res) => {
    if (!req.isAuthenticated()) return res.sendStatus(401);

    try {
      const ticketData = insertSupportTicketSchema.parse({
        ...req.body,
        userId: req.user!.id,
      });

      const ticket = await storage.createSupportTicket(ticketData);
      res.status(201).json(ticket);
    } catch (error) {
      console.error("Error creating support ticket:", error);
      res.status(500).json({ message: "Failed to create support ticket" });
    }
  });

  // Get support tickets
  app.get("/api/support/tickets", async (req, res) => {
    if (!req.isAuthenticated()) return res.sendStatus(401);

    const tickets = await storage.getSupportTickets(req.user!.id);
    res.json(tickets);
  });

  // Chat endpoint
  app.post("/api/chat", async (req, res) => {
    if (!req.isAuthenticated()) return res.sendStatus(401);

    const { message } = req.body;

    if (!message || typeof message !== 'string') {
      return res.status(400).json({ message: "Message is required" });
    }

    const user = req.user!;
    const creditsRequired = Math.max(1, Math.floor(message.length / 100)); // 1 credit per ~100 characters, minimum 1

    if (user.credits < creditsRequired) {
      return res.status(400).json({ 
        message: `Insufficient credits. Required: ${creditsRequired}, Available: ${user.credits}` 
      });
    }

    try {
      // Save user message
      const userMessage = await storage.createChatMessage({
        userId: user.id,
        role: "user",
        content: message,
        creditsUsed: 0,
      });

      // Simple AI response simulation (in production, replace with actual AI API call)
      const aiResponses = [
        "That's a great question! Let me help you with that.",
        "I understand what you're asking. Here's my perspective on this topic.",
        "Based on your message, I can provide some insights that might be helpful.",
        "Thank you for sharing that with me. Let me think about this and provide a thoughtful response.",
        "This is an interesting point you've raised. Let me break this down for you.",
        "I appreciate your question. Here's how I would approach this situation.",
      ];

      const baseResponse = aiResponses[Math.floor(Math.random() * aiResponses.length)];
      const aiResponse = `${baseResponse}\n\nRegarding your message: "${message.length > 100 ? message.substring(0, 100) + '...' : message}"\n\nThis is a demonstration response from the AI assistant. In a production environment, this would be replaced with actual AI processing using services like OpenAI GPT, Anthropic Claude, or similar AI models. The response would be tailored to your specific query and provide relevant, helpful information.`;

      // Save AI response
      await storage.createChatMessage({
        userId: user.id,
        role: "assistant",
        content: aiResponse,
        creditsUsed: creditsRequired,
      });

      // Deduct credits from user
      await storage.updateUser(user.id, {
        credits: user.credits - creditsRequired,
      });

      // Log API usage
      await storage.createApiUsage({
        userId: user.id,
        endpoint: "/api/chat",
        method: "POST",
        creditsUsed: creditsRequired,
        success: true,
      });

      res.json({
        message: aiResponse,
        creditsUsed: creditsRequired,
        remainingCredits: user.credits - creditsRequired,
      });
    } catch (error) {
      console.error("Error processing chat:", error);

      // Log failed API usage
      await storage.createApiUsage({
        userId: user.id,
        endpoint: "/api/chat",
        method: "POST",
        creditsUsed: 0,
        success: false,
      });

      res.status(500).json({ message: "Failed to process chat message" });
    }
  });

  // Get chat history
  app.get("/api/chat/history", async (req, res) => {
    if (!req.isAuthenticated()) return res.sendStatus(401);

    const limit = parseInt(req.query.limit as string) || 50;
    const messages = await storage.getChatMessages(req.user!.id, limit);
    res.json(messages);
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/server-mvc.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import session from "express-session";
import passport from "passport";
import { Strategy as LocalStrategy } from "passport-local";
import mongoose from 'mongoose';
import connectDB from './config/database';
import { config } from './config/environment';
import { setupVite, serveStatic, log } from "./vite";

// Import models
import User, { IUser } from './models/User';

// Import routes
import authRoutes from './routes/authRoutes';
import userRoutes from './routes/userRoutes';
import transactionRoutes from './routes/transactionRoutes';
import supportRoutes from './routes/supportRoutes';
import paymentRoutes from './routes/paymentRoutes';

// Create Express app
const app = express();

// Trust proxy for Replit environment (important for session cookies)
app.set('trust proxy', 1);

// Middleware
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

// Session configuration
app.use(session({
  secret: config.sessionSecret,
  resave: false,
  saveUninitialized: false,
  cookie: {
    secure: config.isProduction,
    maxAge: 24 * 60 * 60 * 1000, // 24 hours
    sameSite: 'lax', // Important for cross-origin requests in development
    httpOnly: true // Security: prevent XSS attacks
  }
}));

// Passport configuration
app.use(passport.initialize());
app.use(passport.session());

// Configure Local Strategy
passport.use(new LocalStrategy(
  {
    usernameField: 'username'
  },
  async (username: string, password: string, done) => {
    try {
      // Find user by username or email
      const user = await User.findOne({
        $or: [{ username }, { email: username }]
      });

      if (!user) {
        return done(null, false, { message: 'Invalid credentials' });
      }

      // Check password
      const isValidPassword = await user.comparePassword(password);
      if (!isValidPassword) {
        return done(null, false, { message: 'Invalid credentials' });
      }

      return done(null, user as any);
    } catch (error) {
      return done(error);
    }
  }
));

// Serialize user
passport.serializeUser((user: any, done) => {
  done(null, user._id);
});

// Deserialize user
passport.deserializeUser(async (id: string, done) => {
  try {
    const user = await User.findById(id).select('-password');
    done(null, user as any);
  } catch (error) {
    done(error, null);
  }
});

// Request logging middleware
app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

// API Routes
app.use('/api', authRoutes);
app.use('/api/user', userRoutes);
app.use('/api/transactions', transactionRoutes);
app.use('/api/support', supportRoutes);
app.use('/api/payments', paymentRoutes);

// Health check endpoint
app.get('/api/health', (req, res) => {
  const mongoStatus = (() => {
    switch (mongoose.connection.readyState) {
      case 0: return 'disconnected';
      case 1: return 'connected';
      case 2: return 'connecting';
      case 3: return 'disconnecting';
      default: return 'unknown';
    }
  })();

  res.json({ 
    status: 'ok', 
    timestamp: new Date().toISOString(),
    server: 'running',
    database: {
      mongodb: mongoStatus,
      url: config.mongodbUrl ? 'configured' : 'not configured'
    },
    environment: config.nodeEnv
  });
});

// Error handling middleware
app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
  const status = err.status || err.statusCode || 500;
  const message = err.message || "Internal Server Error";

  console.error('Error:', err);
  res.status(status).json({ message });
});

// Start server with MongoDB connection
const startServer = async () => {
  try {
    // Try to connect to MongoDB (non-fatal)
    const isDbConnected = await connectDB(config.mongodbUrl);

    // Create HTTP server
    const server = app.listen({
      port: config.port,
      host: "0.0.0.0",
      reusePort: true,
    });

    // Setup Vite in development
    if (config.isDevelopment) {
      await setupVite(app, server);
    } else {
      serveStatic(app);
    }

    log(`ðŸš€ Server running on port ${config.port}`);
    log(`ðŸ“„ Environment: ${config.nodeEnv}`);

    if (isDbConnected) {
      log(`ðŸ—„ï¸  Database: Connected to MongoDB`);
    } else {
      log(`âš ï¸  Database: Not connected - API routes will return errors until database is configured`);
    }

    return server;
  } catch (error) {
    console.error('ðŸ’¥ Failed to start server:', error);
    process.exit(1);
  }
};

// Handle graceful shutdown
process.on('SIGINT', () => {
  log('Received SIGINT. Graceful shutdown...');
  process.exit(0);
});

process.on('SIGTERM', () => {
  log('Received SIGTERM. Graceful shutdown...');
  process.exit(0);
});

// Start the server (ES module compatible check)
if (import.meta.url === `file://${process.argv[1]}`) {
  startServer();
}

export { app, startServer };

