}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }

  .font-heading {
    font-family: var(--font-heading);
  }

  .font-body {
    font-family: var(--font-sans);
  }

  .material-symbols-outlined {
    font-variation-settings: 'FILL' 0, 'wght' 400, 'GRAD' 0, 'opsz' 24;
    font-family: 'Material Symbols Outlined';
    font-weight: normal;
    font-style: normal;
    font-size: 24px;
    line-height: 1;
    letter-spacing: normal;
    text-transform: none;
    display: inline-block;
    white-space: nowrap;
    word-wrap: normal;
    direction: ltr;
    -webkit-font-feature-settings: 'liga';
    -webkit-font-smoothing: antialiased;
  }
}

@layer components {
  .sidebar-link {
    @apply flex items-center px-3 py-2 text-sm font-medium rounded-md transition-colors;
  }

  .nav-link {
    @apply text-foreground hover:text-primary px-3 py-2 text-sm font-medium transition-colors;
  }

  .credit-package {
    @apply bg-card p-6 rounded-lg border hover:border-primary transition-colors cursor-pointer;
  }

  .credit-package.selected {
    @apply border-primary ring-2 ring-primary ring-opacity-20;
  }
}

@layer utilities {
  .animate-pulse {
    animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  @keyframes pulse {
    0%, 100% {
      opacity: 1;
    }
    50% {
      opacity: .5;
    }
  }

  /* Responsive utilities for mobile-first design */
  .responsive-grid {
    @apply grid grid-cols-1 gap-4;
  }

  .responsive-grid-2 {
    @apply grid grid-cols-1 sm:grid-cols-2 gap-4;
  }

  .responsive-grid-3 {
    @apply grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4;
  }

  .responsive-grid-4 {
    @apply grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4;
  }

  .responsive-padding {
    @apply p-4 sm:p-6 lg:p-8;
  }

  .responsive-text {
    @apply text-sm sm:text-base;
  }

  .responsive-heading {
    @apply text-xl sm:text-2xl lg:text-3xl;
  }

  /* Mobile-first responsive breakpoints */
  @media (max-width: 640px) {
    .mobile-full {
      @apply w-full;
    }

    .mobile-hide {
      @apply hidden;
    }

    .mobile-show {
      @apply block;
    }
  }

  @media (min-width: 641px) {
    .mobile-show {
      @apply hidden;
    }
  }
}

